   <script>
    $(document).ready(function() {
        $('#sign-in-code').on('input', function() {
            var code = $(this).val(); // Ambil nilai dari input
            if (code.length === 6) {
                // Jika panjang nilai mencapai 6 digit, lakukan curl (AJAX request)
                $.ajax({
                    url: '<?=site_url('auth/verification')?>', // Ganti dengan URL endpoint Anda
                    method: 'POST', // Sesuaikan metode request jika perlu
                    data: {
                        code: code // Kirimkan data input ke server
                    },
                    success: function(response) {
                        // Tindakan setelah request berhasil
                        console.log('Request successful:', response);
                    },
                    error: function(jqXHR, textStatus, errorThrown) {
                        // Tindakan jika terjadi error
                        console.error('Request failed:', textStatus, errorThrown);
                    }
                });
            }
        });
    });
</script>

